_map:
var boro = parseInt(item.BORO);
var boroDesc = "";
if (boro)
{
    switch(boro)
    {
        case 1:
            boroDesc = 'Manhattan';
            break;
        case 2:
            boroDesc = 'Brooklyn';
            break;
        case 3:
            boroDesc = 'Bronx';
            break;
        case 4:
            boroDesc = 'Queens';
            break;
        case 5:
            boroDesc = 'Staten Island';
            break;

    }
emit(boroDesc,
    { fvLand : parseInt(item.NEW_FV_L), fvTotal : parseInt(item.NEW_FV_T), count: 1});
}

_reduce:
var total  = values.reduce(function(a, b) {

    return { 
        fvLand: (a.fvLand + b.fvLand),
        fvTotal: (a.fvTotal + b.fvTotal),
        count: (a.count + b.count)
        };
    });
emit(total);
